-- create "location" table
CREATE TABLE `location` (`id` bigint NOT NULL AUTO_INCREMENT, `name` varchar(2048) NOT NULL, `description` varchar(2048) NOT NULL, `source` enum('unspecified','minio') NOT NULL, `purpose` enum('unspecified','aip_store') NOT NULL, `uuid` char(36) NOT NULL, PRIMARY KEY (`id`), INDEX `location_name` (`name` (50)), INDEX `location_uuid` (`uuid`)) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- create "package" table
CREATE TABLE `package` (`id` bigint NOT NULL AUTO_INCREMENT, `name` varchar(2048) NOT NULL, `aip_id` char(36) NOT NULL, `status` enum('unspecified','in_review','rejected','stored','moving') NOT NULL, `object_key` char(36) NOT NULL, `location_id` bigint NULL, PRIMARY KEY (`id`), INDEX `pkg_aip_id` (`aip_id`), INDEX `pkg_object_key` (`object_key`), CONSTRAINT `package_location_location` FOREIGN KEY (`location_id`) REFERENCES `location` (`id`) ON DELETE SET NULL) CHARSET utf8mb4 COLLATE utf8mb4_bin;
